package org.mozilla.universalchardet;import java.io.DataInputStream;import java.io.IOException;import java.io.InputStream;import org.mozilla.universalchardet.UniversalDetector;public class CharsetDetectUtil {		private static UniversalDetector detector=new UniversalDetector(null);		public static String getCharsetFromBytes(byte[] content) {		if(null == detector) {			detector=new UniversalDetector(null);		}		if (null != content && content.length > 0 && !detector.isDone()) {			detector.handleData(content, 0, content.length);		}		detector.dataEnd();		String encoding=detector.getDetectedCharset();		detector.reset();		return encoding;	}		public static String getCharsetFromStream(InputStream stream) {		if(null == stream) {			return null;		}		DataInputStream dis = new DataInputStream(stream);		byte[] buf=new byte[1024];		try {			dis.read(buf);			dis.close();		} catch (IOException e) {			e.printStackTrace();		}		return getCharsetFromBytes(buf);	}}