package six.lwuikit.alphabet;import android.widget.AbsListView;import android.widget.Adapter;import android.widget.HeaderViewListAdapter;import android.widget.ListView;import android.widget.SectionIndexer;public class AlphabetUtil {		public static void scrollByAlphabet(AbsListView list,AlphabetView alphabet,String mCurrentDisplayLetter) {		Adapter adapter = list.getAdapter();		SectionIndexer mSectionIndexer = null;		int mListOffset = 0;		Object[] mSections;		if (adapter instanceof HeaderViewListAdapter) {			mListOffset = ((HeaderViewListAdapter) adapter).getHeadersCount();			adapter = ((HeaderViewListAdapter) adapter).getWrappedAdapter();		}		if (adapter instanceof SectionIndexer) {			mSectionIndexer = (SectionIndexer) adapter;			mSections = mSectionIndexer.getSections();		} else {			mSections = new String[] {"#"};		}		int count = list.getCount();		final Object[] sections = mSections;		String letter = mCurrentDisplayLetter;		final int nSections = sections.length;		int section = -1;		for (int i = 0; i < nSections; i++) {			if (letter.equals(sections[i])) {				section = i;				break;			}		}		if (section == -1) {			return;		}		int index = mSectionIndexer.getPositionForSection(section);		if (index > count - 1 || index < 0) {			index = count - 1;		}		((ListView) list).setSelectionFromTop(index + mListOffset, 0);	}}