package six.lwuikit.alphabet;import java.util.HashMap;import android.widget.SectionIndexer;public class ExSectionIndexer implements SectionIndexer{	private HashMap<String, Integer> mAlphaIndexer;	private String[] mSections;	public ExSectionIndexer(HashMap<String, Integer> alphaIndexer, String[] sections) {		mAlphaIndexer = alphaIndexer;		mSections = sections;	}		public HashMap<String, Integer> getAlphaIndexer() {		return mAlphaIndexer;	}		@Override	public Object[] getSections() {		return mSections;	}	@Override	public int getPositionForSection(int section) {		String letter = mSections[section];		return mAlphaIndexer.get(letter);	}	@Override	public int getSectionForPosition(int position) {		int length = mSections.length;		for (int i = 0; i < length; i++) {			int start = mAlphaIndexer.get(mSections[i]);			int end = mAlphaIndexer.get(mSections[i + 1 == length ? i : i + 1]);						if ((position >= start && position < end) ||				(start == end && position >= start)) {				return i;			}		}		return 0;	}	public void release() {		mAlphaIndexer = null;		mSections = null;	}}